        data() {
            return {
                showPreloader: false
            }
        },
        computed: {
            preloadProgress: function () {

            }
        },
        methods: {
            getSpecialities() {
                this.$axios({
                    method: 'get',
                    url: '/api/vendor/specialities'
                }).then((response) => {
                    this.$store.commit('setAvailableSpecialities', response.data)
                })
            },
        },
        updated() {
            console.log('route', this.$route);
            // if(this.$store.state.desktop && (this.$route.name !== 'login' || this.$route.name !== 'cabinet' || this.$route.name !== 'desktop')) {
            //     console.log(this.$route)
            //     this.$router.push('/desktop/');
            // }
        },
        mounted() {
            if (JSON.parse(window.localStorage.getItem('isDesktopView'))) {
                this.$store.commit('setDesktopView', true)
            } else {
                this.$store.commit('setDesktopView', false)
            }
            const bodyNode =  document.body;
            // const wrapperNode =  document.querySelector('.wrapper');
            if(false && !window.localStorage.getItem('preloaderShowed')) {
                this.showPreloader = true;
                bodyNode.style.overflow = 'hidden';
                setTimeout(() => {
                    this.showPreloader = false;
                    window.localStorage.setItem('preloaderShowed', 'Y');
                    bodyNode.removeAttribute('style');
                    // wrapperNode.removeAttribute('style');
                }, 400)
            }
            // this.getSpecialities();
        }
    }
</script>
